jcr:root.optionsProvider:
function(path, record) {
    var response = CQ.HTTP.get(this.findParentByType('dialog').path+".typeoptions.json");
    if(CQ.utils.HTTP.isOk(response)){  
        var responseBody = response.body;
        var jsonData = JSON.parse(responseBody);    
        return jsonData;    
    }
    console.log("request for user group options was not ok: "+response.status+" - "+response.body);
    return [];
};

jcr:root.selectionchanged:
function(field,value){
	var panel = field.findParentByType('tabpanel');
	if(field.comboBox.selectedIndex>=0){
		var prt = panel.find('name','./parentResourceType')[0];
		var ep = panel.find('name','./endpoint')[0];
		if(prt && ep){
			var options = field.optionsProvider();
			ep.setValue(options[field.comboBox.selectedIndex].endpoint);
			prt.setValue(options[field.comboBox.selectedIndex].parentResourceType);
		}
	}
	var child = panel.find('name','./resourceType')[0];
	if(child){
		if (value=='other') {
			child.show();
			panel.doLayout();
		} 
		else {
			child.hide();
		}
	}
}

listeners.selectionchanged:
function(field,value){
	var panel = field.findParentByType('tabpanel');
	var prt = panel.find('name','./parentResourceType')[0];
	var ep = panel.find('name','./endpoint')[0];
	var mode = panel.find('name','./mode')[0];
	if(prt && ep && mode){
		if(mode.getValue()=='createContent'){
			ep.setValue(field.optionsProvider()[field.comboBox.selectedIndex].endpoint);
		} else if(mode.getValue()=='editContent'){
			ep.setValue(field.optionsProvider()[field.comboBox.selectedIndex].editEndpoint);
		}
		prt.setValue(field.optionsProvider()[field.comboBox.selectedIndex].parentResourceType);
	}
	var child = panel.find('name','./resourceType')[0];
	if(child){
		if (value=='other') {
			child.show();
			panel.doLayout();
		} 
		else {
			child.hide();
		}
	}
}
