jcr:root.selectionchanged:
function(field,value){
	var panel = field.findParentByType('tabpanel');
	var formurl = panel.find('name','./formurl')[0];
	var selector = panel.find('name','./selector')[0];
	if (value=='component') {
		if(formurl){
			formurl.hide();
		}
		if(selector){
			selector.show();
		}
		panel.doLayout();
	} else if(value=='form'){
		if(formurl){
			formurl.show();
		}
		if(selector){
			selector.hide();
		}
		panel.doLayout();
	} else {
		if(formurl){
			formurl.hide();
		}
		if(selector){
			selector.hide();
		}

	}
}

